<template>
  <div id="user-container">
    <div class="container">
      <div class="img-container">
        <img :src="userInfo.userImg" alt="" class="userImg" />
      </div>
      <span class="welcomeText">欢迎您，{{ this.userInfo.userName }}</span>
      <el-row>
        <el-button type="warning" round id="logout-btn" @click="logout"
          >退出登录</el-button
        >
      </el-row>
    </div>
  </div>
</template>

<script>
import { mapActions } from "vuex";
import { mapState } from "vuex";

export default {
  data() {
    return {
      initInfo: {
        userName: "未登录",
        userImg:
          "data:image/png;base64,",
      },
    };
  },
  methods: {
    ...mapActions(["loginAction"]),
    logout() {
      this.loginAction(this.initInfo);
      this.$router.push("/login");
    },
  },
  computed: {
    ...mapState(["userInfo"]),
  },
};
</script>

